import classes from './Banner.module.css'
import React from 'react'
import Typical from 'react-typical';
import FormSection from "../ui/FormSection";

class Banner extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            accountAddress: '',
            userConnected: false,
            blogPost: null,
            postCount: 0,
            posts: [],
            loading: true,
            postLoading: true
        }

        this.createPost = this.createPost.bind(this)
        // this.tipPost = this.tipPost.bind(this)
    }
    async createPost(title, content) {
        this.setState({postLoading: true})
        this.state.blogPost.methods.createPost(title, content).send({from: this.props.input.toString()})
            .once('receipt', (receipt) => {
                this.fetchData()
                this.setState({postLoading: false})
            })

    }
    render() {
        return (

            <div id={classes.box} className={' p-0 p-sm-5'}>

                <div id={classes.heading} className={' p-0 pt-sm-5'}>

                    An Ask Question App <br/> where you can
                </div>
                    <div id={classes.type}>
                        <Typical
                            loop={Infinity} // Infinity
                            wrapper={"b"}
                            steps={[' Ask', 1000, ' Comment', 1000, ' Like', 1000]}
                        />
                    </div>
                {!this.props.input ? <div/>
                    :
                    <FormSection createPost={this.createPost} address={this.props.input}/>
                }
            </div>
        )
    }
}

export default Banner;
