// import classes from './PostItem.css'
import React from 'react'
import {Card, Button} from 'react-bootstrap'
import CardFormatter from "../ui/CardFormatter";
import ImageProfile from "../ui/ImageProfile";
import './PostItem.css'

function PostItem(props) {


    const id = props.id.toString();

    function onClickHandler() {

    }

    window.addEventListener('load', () => {
        const elem = '.heart'.concat(id);
        var style = document.createElement(elem);
        elem.innerHTML = ` float: left;
    display: inline-block;
    cursor: pointer;
    height: 40px;
    width: 40px;
    /*align-content: center;*/
    background-image: url('https://abs.twimg.com/a/1446542199/img/t1/web_heart_animation.png');
    background-repeat: no-repeat;
    background-size: 2900%;
    scale: 2;`;
        document.head.appendChild(style);
    })

    function clickListener(id) {
        /* when a user clicks, toggle the 'is-animating' class */
        var heart = document.querySelector('.heart'.concat(id));
        // heart.classList.toggle("is_animating");

    }


    return (
        <CardFormatter>
            <Card onClick={onClickHandler} key={props.id}>
                <Card.Header> <ImageProfile class="image" width={'50'} height={'50'}
                                            account={props.author.toString()}/> {props.author}</Card.Header>
                <Card.Body>
                    <Card.Title><strong>{props.title} </strong></Card.Title>
                    <Card.Text>
                        {props.content}
                    </Card.Text>
                </Card.Body>
                <Card.Footer>
                    <div key={props.id} onClick={clickListener(id)} class={"heart".concat(id)}/>
                    <i className="far fa-comment"/>
                    <div className="numberOfComments"> {props.numberOfComments}</div>
                    <button class="buttonComment"> Comment</button>
                </Card.Footer>
            </Card>

        </CardFormatter>


    )
}

export default PostItem
