// import classes from './PostItem.css'
import React from 'react'
import {Card, Button} from 'react-bootstrap'
import CardFormatter from "../ui/CardFormatter";
import ImageProfile from "../ui/ImageProfile";
import './PostItem.css'

function PostItem(props) {

    function onClickHandler() {
     let id = props.id.toString();
    }

    function clickListener() {
        /* when a user clicks, toggle the 'is-animating' class */
        var heart = document.querySelector('.heart');
        heart.classList.toggle("is_animating");

    }


    return (
        <CardFormatter>
            <Card onClick={onClickHandler} key={props.id} >
                <Card.Header> <ImageProfile class="image" width={'50'} height={'50'}
                                            account={props.author.toString()}/> {props.author}</Card.Header>
                <Card.Body>
                    <Card.Title><strong>{props.title} </strong></Card.Title>
                    <Card.Text>
                        {props.content}
                    </Card.Text>
                </Card.Body>
                <Card.Footer>
                    <div  key={props.id} onClick={clickListener} class={"heart".concat(id)}/>
                    <i className="far fa-comment"/>
                    <div className="numberOfComments"> {props.numberOfComments}</div>
                    <button class="buttonComment"> Comment</button>
                </Card.Footer>
            </Card>

        </CardFormatter>


    )
}

export default PostItem
